mapper.select:
SELECT noticesno, noticenumber, imageurl, receiptdate, species, gender, discoveryplace, characteristic, state, publicnoticestart, publicnoticeend, colorcd, age, weight, orgnm, careaddr, officetel, carenm
FROM NOTICES
WHERE noticesno = #{noticesno}

SELECT noticesno, noticenumber, imageurl, receiptdate, species, gender, discoveryplace, characteristic, state, publicnoticestart, publicnoticeend, colorcd, age, weight, orgnm, careaddr, officetel, carenm, r FROM ( SELECT noticesno, noticenumber, imageurl, receiptdate, species, gender, discoveryplace, characteristic, state, publicnoticestart, publicnoticeend, colorcd, age, weight, orgnm, careaddr, officetel, carenm, rownum as r FROM ( SELECT noticesno, noticenumber, imageurl, receiptdate, species, gender, discoveryplace, characteristic, state, publicnoticestart, publicnoticeend, colorcd, age, weight, orgnm, careaddr, officetel, carenm FROM NOTICES
WHERE (UPPER(noticenumber) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(species) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(discoveryplace) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(characteristic) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(colorcd) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(orgnm) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(careaddr) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY noticesno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
